float circle(vec2 uv,float radius, vec2 shapePos)
{

    float dist = distance(shapePos,uv);
    float value = step(radius,dist);

    return value; 
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;	 
    float ratio = iResolution.x / iResolution.y;

    vec2 shapePos = vec2(.5,.5);
    shapePos.x *= ratio;
    uv.x *= ratio;

    float radius = .2;

    //vec2 shapePos = vec2(.5 * ratio, .5);
    //float dist = distance( shapePos,uv);
    //float radius = .2;
    //float value = step(radius,dist);

    float value = circle(uv,radius *(sin(sin(iTime *3.0 )* 2.5)+1.0),shapePos);
    vec3 color = vec3(value,1,0);
    fragColor = vec4(color,1.0);
}
