void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float a = mod(iTime / 128.0, 6.28);
    float r = 0.385;
    float theta = a;
    float degs = theta * 180.0 / 3.14;

    vec2 uv = fragCoord / iResolution.xy;
    vec2 c = vec2(r * cos(degs), r * sin(degs));
    vec2 z = vec2(uv.x * 3.5 - 1.75, uv.y * 2.0 - 1.0);
    vec3 col = vec3(0);
    int iter = 0;
    int max_iter = 100;

    while (iter < max_iter && z.x*z.x + z.y*z.y < 4.0) {
    	float newzre = z.x * z.x - z.y*z.y + c.x;

        z.y = 2.0 * z.x * z.y + c.y;
        z.x = newzre;

        iter++;
    }

    if (iter < max_iter) {
    	col = vec3(0.1 + float(iter) / float(max_iter));
    }

    fragColor = vec4(col,1.0);
}
