#define S(a,b,t) smoothstep(a,b,t)
#define PI 3.14

float N(float t)
{
    return fract(sin(t*3456.)*6547.);
}

vec3 Square(vec2 uv, vec2 pos, float size, vec3 color, float blur, float t)
{
	float square = 1.;

    if (t > 0.){
    	color *= .9;
    }
    uv.x -=pos.x;
    uv.x /= t;
    uv.x +=pos.x;
    square =  S(size+blur,size-blur,uv.x-pos.x);
    square *= S(-size-blur,-size+blur,uv.x-pos.x);
    square *= S(size+blur,size-blur,uv.y-pos.y);
    square *= S(-size-blur,-size+blur,uv.y-pos.y);

	return square*color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;

    vec3 col = vec3(0);  

    vec2 offset = vec2(-0.838888,-0.45);

    for (int x; x<30; x++){
        for (int y; y<10; y++)
        {           
            float t = 3.*sin(iTime*.5+(float(x)/30.)*-1.7778+(float(y)/10.));

			t = clamp(t,-1.,1.);

            col += Square(uv, vec2(float(x)*0.1+offset.x, float(y)*0.1+offset.y), 0.047, vec3(.84,.1,.4)*(t+2.), 0.001, t), 1.;
        }

    }


   	fragColor = vec4(col,1.);

}
