void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	vec2 uv = fragCoord.xy / iResolution.xy;
	float y = 
		0.7*sin((uv.y + iTime) * 4.0) * 0.038 +
		0.3*sin((uv.y + iTime) * 8.0) * 0.010 +
		0.05*sin((uv.y + iTime) * 40.0) * 0.05;

	float x = 
		0.5*sin((uv.y + iTime) * 5.0) * 0.1 +
		0.2*sin((uv.x + iTime) * 10.0) * 0.05 +
		0.2*sin((uv.x + iTime) * 30.0) * 0.02;

	fragColor = texture(iChannel0, 0.79*(uv + vec2(y+0.11, x+0.11)));
}
