#define CS(a) vec2(cos(a), sin(a))
#define PI 3.14159265

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.x;

    vec2 p1 = vec2(p); 
    p1 += CS(iTime) * 0.2;
	float r1 = length(p1) * 8.0; 

    vec2 p2 = vec2(p);
    p2 += CS(iTime + PI) * 0.2;
    float r2 = length(p2) * 8.0;

    float green = 1.0 - r1;
    float red = 1.0 - r2;
	fragColor = vec4(red, green, 0.0, 1.0);
}
