void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;

    float bpm = 1.0 / 60.0 * 220.0;
    float t = iTime;
    float bt = t * bpm;
    float pi = 3.14159265359;
    float t0 = texture(iChannel0, vec2(0.0, 0.25)).x;
    float t1 = texture(iChannel0, vec2(0.8, 0.25)).x;

    float t0c = pow(max(t0 - 0.35, 0.0), 0.8) * 2.0;
    float t1c = pow(max(t1 - 0.05, 0.0), 0.8) * 2.0;

    float tx0 = texture(iChannel1, nuv + vec2(sin(t0c), cos(t1c))).x;

    vec3 c = vec3(0.0);

    nuv.y += t0c * 0.03;
    nuv = nuv + nuv * sin(nuv.x * 20.0 + bt) * t1 * 0.6;

    float nuvtanx = 1.0 - pow(abs(tanh(nuv.y)), 2.0) * 3.1 + tx0 * t0c * t1c * 0.3 * (1.0 - abs(nuv.y));

    c.y = t0c * t1c * abs(nuvtanx * (0.5 + t0 * 1.75 + t1 * 0.15));
    c.z = t1c;

    c.xyz += smoothstep(0.0, 1.0, pow(sin(nuvtanx * 32.0 + bt), 3.0)) * 0.5 + t0c * 0.05;
    c.x -= 0.5 * tx0 * t1c;

    c -= abs(nuv.y) * 1.2;
	c.y -= abs(nuv.y) * 1.2;  
    c.x = max(c.x, 0.0);
    c.y = max(c.y, 0.0);
    c.z = max(c.z, 0.0);

    fragColor.xyz = c;
}
