//Ethan Alexander Shulman 2017



#define draw(dc) c = mix(dc,c,clamp(d/pixelSize, 0., 1.))
#define rot(a) mat2(sin(a),cos(a),-cos(a),sin(a))


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pixelSize = length(1.0/iResolution.xy);
	vec2 uv = (fragCoord*2. - iResolution.xy)/iResolution.x;


    vec3 c = vec3(1.);//start with white background

	//ring index
    float len = floor(length(uv)*10.0)/10.0+0.02;

    //ring
    float snd = pow(texture(iChannel0, vec2(fract(atan(uv.x,uv.y)/6.28+0.5+iTime*len*0.04),0.)).x,2.2);
    float d = abs(length(uv)-len-mod(snd*len*0.2,0.08))-0.01;
    draw(vec3(0.));

    //output color
    fragColor = vec4(c, 1.);
}
