void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 coord = fragCoord.xy / iResolution.xy;
	coord = (coord - vec2(0.5)) * vec2(2.0);
	coord.y *= -1.0;
	float sz = 0.3;
	float sw = 1.0;
	float f = 2.0 + 0.3 * sin(iTime);
	float x = coord.x * f;
	float y = coord.y * f;
	float p = x*x + y*y - sz;
	float len = p*p*p + sw*x*x*y*y*y;
	len = clamp(len * 100000000.0, 0.0, 1.0);
	float alp = ceil(1.0 - len);
	float col = alp;
	fragColor = vec4(col, 0.0, 0.0, 1.0);
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
}
