float PI=3.14159;

vec3 dcol=vec3(-1.);

float sine(float x){
	return pow(sin(x*PI/2.),2.);
	return (sin((x-0.5)*PI)+1.)/2.;
}

// (current position, polygon center, polygon sides num, polygon "radius"
float poly(vec2 uv, vec2 c, float s, float r){
	vec2 d = uv-c;

	float ang = atan(d.x,d.y)+PI;
	//ang = cos(abs(mod(ang,1./s)*s-0.5)*2.);

	//dcol.r=mod(ang/2.,PI/s);

	return length(d)+ang/(sin(iTime)/PI+1.+1.);
}

float spade(vec2 uv){
	uv.y+=0.5;
	float ang = abs(atan(uv.x,-uv.y)/PI);

	float slices = 50.;
	float angmod = sine(mod(ang,2./slices)*slices-1.);

	angmod = min(angmod,length(uv)*1.5-0.4);

	angmod=angmod*2.+sin(iTime);



	float c = length(uv)/ang;	

	float modu=10.*ang;

	c = modu*(c-1.);

	c = c * angmod;

	//float br = abs(ang*(1.-length(uv)))*20.-6.;

	return c;

	return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord.xy-iResolution.xy) / iResolution.y;


	vec3 color = vec3(spade(uv));

	//For debugging purposes
	if(any(greaterThanEqual(dcol,vec3(0.)))) color=dcol;

	fragColor = vec4(color,1.0);
}
