//Mirros is a line y=Ax+b
#define speed1 1.
#define speed2 1.


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	float height = iResolution.y;
	bool above = (fragCoord.y > height/2.);
	vec2 P = fragCoord.xy / iResolution.xy;
	//float mirror = uv.x*MIRROR_A + MIRROR_B;

	vec2 P0 = vec2(0., cos(speed1*iChannelTime[0]));
	vec2 P1 = vec2(1., sin(speed2*iChannelTime[0]));
	vec2 w = P-P0;
	vec2 vL = P1-P0;
	vec2 d = w - (dot(w,vL)/dot(vL,vL))*vL;

	float under =  -d.y;
    under = clamp(under*100000., 0.,1.);
	P = abs(P - 2.*d*under);



	vec4 color = texture(iChannel0, P);
	//color = vec4(color.rgb*under,1);

	fragColor = color;
}
