//Mirros is a line y=Ax+b
#define MIRROR_P0X 0.
#define MIRROR_P0Y 0.
#define MIRROR_P1X 1.
#define MIRROR_P1Y 1.


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


	float height = iResolution.y;
	bool above = (fragCoord.y > height/2.);
	vec2 P = fragCoord.xy / iResolution.xy;
	//float mirror = uv.x*MIRROR_A + MIRROR_B;

	vec2 P0 = vec2(MIRROR_P0X, MIRROR_P0Y);
	vec2 P1 = vec2(MIRROR_P1X, MIRROR_P1Y);
	vec2 w = P-P0;
	vec2 vL = P1-P0;
	vec2 d = w - (dot(w,vL)/dot(vL,vL))*vL;

	float under =  -d.y;
    under = clamp(under*100000., 0.,1.);
	P = abs(P - 2.*d*under);



	vec4 color = texture(iChannel0, P);
	//color = vec4(color.rgb*under,1);

	fragColor = color;
}
