//float segm( float a, float b, float c, float x );
float segm( float a, float b, float c, float x )
{
    return smoothstep(a-c,a,x) - smoothstep(b,b+c,x);
}
vec2 m_fragCoord;
#define Thickness 0.006
float drawLine(vec2 p1, vec2 p2) {
  vec2 uv = m_fragCoord.xy / iResolution.xy;

  float a = abs(distance(p1, uv));
  float b = abs(distance(p2, uv)); 
  float c = abs(distance(p1, p2));

  if ( a >= c || b >=  c ) return 0.0;

  float p = (a + b + c) * 0.5;

  // median to (p1, p2) vector
  float h = 2.0 / c * sqrt( p * ( p - a) * ( p - b) * ( p - c));

  return mix(1.0, 0.0, smoothstep(0.5 * Thickness, 1.5 * Thickness, h));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    m_fragCoord = fragCoord;
	vec2 uv = fragCoord.xy / iResolution.xy;	
	//Wiskers variable
	float color = 0.0;
	//Re-positioning for eyes and smile
	uv.x -= 0.5;
	uv.y -= 0.5;
	//Radius and Diameter for eyes
	float r = length(uv);	
	float d = 0.1;
	// Movement Variables
	float b = 0.2 + 0.4*sin(4.0+(iTime*2.3));
	float b2 = 0.13 + 0.2*sin(4.0+(iTime*6.3));
	float w = 1.1 + 0.1*sin(6.0+(iTime*6.3));
	//Eye and smile posistion
	vec2 eyepos = vec2(abs(uv.x)-0.13, (uv.y-0.05)*(1.0+0.5*b));
	vec2 smilepos = vec2( ( uv.x),(uv.y+0.15)*(0.4 +1.0)* (1.0+2.0*b2) );
	//Inner eye outer eye and smile
	float eye = 1.0 - segm (0.13, 0.1, 0.1,  length(eyepos));
	float eye2 = 1.0 - segm (0.05, 0.1, 0.01, length(eyepos));			
	float smile = 1.0 - segm (0.19, 0.2, 0.01,  length(smilepos)*step(smilepos.y,0.0));
	//Wiskers right		
	color = drawLine(vec2(0.98, 0.4*w),       vec2(0.78, 0.5));
	color +=drawLine(vec2(0.98, 0.6*w-0.1),   vec2(0.78, 0.5));
	color +=drawLine(vec2(0.98, 0.41*w-0.08), vec2(0.78, 0.41));
	color +=drawLine(vec2(0.98, 0.37*w-0.08), vec2(0.78, 0.37));
	//Wiskers left	
    color +=drawLine(vec2(0.02, 0.6*w-0.1),   vec2(0.22, 0.5));
    color +=drawLine(vec2(0.02, 0.4*w),       vec2(0.22, 0.5));
	color +=drawLine(vec2(0.02, 0.41*w-0.08), vec2(0.22, 0.41));
	color +=drawLine(vec2(0.02, 0.37*w-0.08), vec2(0.22, 0.37));
	//Mixing the wiskers inner/outer eyes and smile		                     
	vec3 col = mix( vec3(-color,color,eye2), vec3(smile, eye, 0.0), 0.5);
	//Draw :)
    float Z = 1.0;
	fragColor = vec4(col*col.z*(1.5)*Z*(1.6)*col.x*(1.5)*fragCoord.y*(0.006),1.0);	
}
