vec4 noise(vec2 v) {
	vec4 c = vec4(0.0,0.0,0.0,0.0);
	float s = 0.0;
	for(float i=1.0;i<16.0;i++) {
		float q = pow(2.0,i);
		c+=texture(iChannel0,v*pow(0.5,i))*q;
		s+=q;
	}
	return c/s;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float n = iTime;
	vec2 uv = fragCoord.xy+iMouse.xy;

	float p = 100.0;
	vec4 v = noise(noise(uv).xy*100.0*(10.0+p*sin(n/p)));
	fragColor = (v.x-v.y)*vec4(1,1,1,1);
}
