//Ethan Alexander Shulman 2016

//Fork from 'pumpkin carving 2016' https://www.shadertoy.com/view/XltXzr


#define brushSizee 10.
#define brushOpacity 0.25

#define brushSize (brushSizee*length(iResolution.xy)/256.)


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //initialize blank for first 60 frames to give people time to go fullscreen
    if (iFrame < 60) {
        fragColor = vec4(0.,0.,0.,1.);
        return;
    }
    if (fragCoord.x+fragCoord.y < 2.) {
        //store old mouse position in top left pixel for smoothed line drawing
        fragColor = vec4(iMouse.xy,clamp(iMouse.w*1e4,0.,1.),0.);
        return;
    }

    vec2 uv = fragCoord/iResolution.xy;
    vec4 v = texture(iChannel0, uv);


    //smooth line paint
    vec2 currDelta = iMouse.xy-fragCoord;
    float len = length(currDelta)-brushSize;
    if (iMouse.w > 0.) {

        vec3 old = texture(iChannel0, vec2(.5/iResolution.xy)).xyz;
        old.xy = mix(iMouse.xy,old.xy,old.z);
        vec2 oldDelta = fragCoord-old.xy,
             newDelta = iMouse.xy-old.xy;

        //line from last mouse pos to new pos      
        len = length(oldDelta-newDelta*
                     clamp(dot(oldDelta,newDelta)/dot(newDelta,newDelta),0.,1.))-brushSize;

        //paint line with last area cutout and fade
        float subLen = mix(0.,length(oldDelta)-brushSize,old.z);
        float brush = brushOpacity*clamp(-(len-min(0.,subLen))/brushSize,0.,1.);
        v.xyz += brush;
       	v.xyz = min(v.xyz,1.);
    }

    fragColor = v;
}
