float remap(float low1, float high1, float low2, float high2, float value){
    return low2 + (value - low1) * (high2 - low2) / (high1 - low1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float aspectRatio = iResolution.x / iResolution.y;

    float t = remap(-1.0,1.0, 0.0, 1.0, sin(iTime * 0.1) );
    fragCoord.x *= abs(fragCoord.y - iResolution.y * mix(0.0,1.0,t)) * 0.01;




    float tx = -0.5 * iResolution.x;// * aspectRatio;
    float ty = -0.5 * iResolution.y;
    mat3 trans = mat3(
        1.0, 0.0, tx,
        0.0, 1.0, ty,
		0.0, 0.0, 1.0
	);

    float fi = iTime * 0.3;
    mat2 rot = mat2(
        cos(fi), -sin(fi),
        sin(fi), cos(fi)
	);

    fragCoord = (vec3(fragCoord,1) * trans).xy;
    fragCoord = fragCoord * rot;
    fragCoord = (vec3(fragCoord,1) * -trans).xy;




    vec2 uv2 = fragCoord.xy / iResolution.xy;
    float value = remap(-1.0,1.0, 0.0, 1.0, sin(iTime * 0.1) );
    vec4 tex0 = texture(iChannel0, mix(uv,uv2,value));
    //tex0 = texture(iChannel0, uv2);

    fragColor = tex0;
}
