void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

vec2 fc;
vec4 rd(vec2 p)
{

    return texture(iChannel1, (fc+p)/iResolution.xy);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    fc = fragCoord;
    ivec2 p = ivec2(fc);
	vec2 uv = fragCoord.xy / iResolution.xy;
    int mode = (iFrame / 2)%2;
    int localMode = (p[mode] + iFrame)%2;
    vec2 c = vec2(0.0);
    vec4 self = rd(c);
    if(localMode == 0)
        localMode = -1;
    c[mode] = float(localMode);
    vec4 src = rd(c);
    if(sign(src[mode] - self[mode]) == sign(float(localMode)))
    {
        self = src;
    }
    //fragColor = vec4(c, 0.0, 1.0);
    fragColor = self;
    if(iFrame < 1)
		fragColor = vec4(texture(iChannel0, uv).rgb, 0.0);
}
