#define PI 3.141592654
#define t iTime
#define speed 4.
#define size 50.
#define start_split .2
#define split 1.2
#define fade 5.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord.xy / iResolution.xy;

    // distance from mouse pointer
    float dist = length(iMouse.xy - fragCoord.xy) / iResolution.y,
             s = start_split + split*dist,
         phase = dist*size - t*speed;

	fragColor = vec4(.55, .5, .55, 0)
              + vec4(.3, 1, .3, 0) * sin(phase
              + s * vec4(0, 1, 2, 0)) / (dist * fade);
}
