float icon_speaker(ivec2 p) {
    if (min(p.x,p.y)<0 || max(p.x,p.y)>=22) return 1.;
    //if (min(p.x,p.y)<1 || max(p.x,p.y)>=21) return 0.5;

    p.y = abs(p.y-10);
    if (p.x>19) return 1.;
    if (p.x>15) {
        //return 0.;
    	if (p.y==1) return 0.;
    	if (p.x/2-5==p.y) return 0.;
    	if (p.x-11==p.y) return 0.;
    }
    if (p.x<3||p.x>13||p.y>7||p.x==5||max(2,p.x-5)<p.y) return 1.;
    return 0.;
}

float icon_fullscreen(ivec2 p) {
    if (min(p.x,p.y)<0 || max(p.x,p.y)>=22) return 1.;
    //if (min(p.x,p.y)<1 || max(p.x,p.y)>=21) return 0.5;
    p = (abs(2*p-21)-1)/2;
    int a = min(p.x,p.y), b = max(p.x,p.y);
    return float(b<3||a<1||b>6);
}

float icon_record(ivec2 p) {
    if (min(p.x,p.y)<0 || max(p.x,p.y)>=22) return 1.;
    //if (min(p.x,p.y)<1 || max(p.x,p.y)>=21) return 0.5;
