        vec2 d = vec2(p);
        return clamp(length(d*vec2(1,10./11.)-vec2(11,5))-4.,0.,1.);
    }
    p.x-=2;
    p.y-=13;
    if (p.x<0||p.y<0||p.y>6) return 1.;
