float bgpattern( in vec2 p ) {
    // https://www.shadertoy.com/view/XljSRK
    vec2 uv = p + 0.1*texture( iChannel2, 0.05*p ).xy;
    return texture( iChannel1, 16.0*uv ).x;
}

void background(out vec3 f, vec2 d) {
    vec2 p = d/1000.;
	// https://www.shadertoy.com/view/XljSRK

    float fa = bgpattern( (d+0.0)/512. );
    float fb = bgpattern( (d-0.5)/512. );

    f = vec3( 0.822 + 0.4*(fa-fb) );
}

