void box(inout vec3 f, vec2 p, ivec4 b, float r, vec3 k) {
    vec4 g = vec4(b);
    vec2 a = p-g.xy-g.zw/2.+vec2(0.5);
    vec2 d = g.zw/2.-r+0.0;
    float c = length(max(abs(a)-d,0.0))-r-0.5+0.0;
    f = mix(f, k, clamp(0.-c,0.,1.));
}

bool boxtest(ivec2 p, ivec4 b) {
    p-=b.xy;
    return p.x>=0&&p.x<=b.z&&p.y>=0&&p.y<=b.w;
}

