void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy; // Condensing this into one line
       //xy.y = 1.0-xy.y; // Flipping the y
    vec4 texColor = texture(iChannel0,xy); // Get the pixel at xy from iChannel0

    vec4 bgColor = vec4(13.0, 160.0, 38.0, 1.0) / 256.0;

    vec4 deltaColor = abs(bgColor - texColor);
    float bgFault = 0.2;

    if ( deltaColor.r <= bgFault && deltaColor.g <= bgFault && deltaColor.b <= bgFault) {
		texColor = texture(iChannel1,xy);
        float stripe = sin((fragCoord.x + iTime*100.0 ) / 14.0) + 1.0;
        if (stripe > 1.1) stripe = 1.1;
        texColor *= stripe;
    }

    fragColor = texColor; // Set the screen pixel to that color
}
