void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy;
  	uv /= vec2(iResolution.y / iResolution.x, 1);

  	float d = distance(abs(uv),vec2(0.5*cos(2.7*iTime),sin(iTime)));

  	float d2 = distance(abs(uv),vec2(0.5*sin(4.7*iTime),cos(3.0*iTime)));

  	d = max(d,d2);

  	float f2 = texture( iChannel0, vec2(20.0)).r * 1.0;

  	vec3 color = vec3(0.0);

  	vec2 pos = vec2(0.5)-uv;

  	float r = length(pos)*2.0+sin(iTime);
  	float a = atan(pos.y,pos.x);

  	float f = abs(cos(20.0*iTime+a*3.+.1*iTime*f2))*sin(iTime)+0.4*sin(d);

  	color = vec3( f*sin(iTime)+uv.x-r-smoothstep(f,f+0.02,r) );
  	color = mix(color,vec3(f/2.0,tan(pow(d,4.0)),1.0),f);

    vec2 brituv = uv;

    brituv.x /= 2.0*abs(sin(iTime))+0.5;
 	vec4 vid = texture(iChannel0,brituv);
    vid.r += sin(iTime);
  	color = mix(color, vid.rgb, abs(sin(iTime)));

	fragColor = vec4( color, 1.0 );
}
