vec4 intTest(vec2 zap)
{
	float x = zap.x;
	float y = zap.y;

	float br = 0.0;


	for (float i = 0.0; i < 64.0; i++)
	{
		for (float j = 0.0; j < 64.0; j++)
		{
			vec2 uv2 = vec2((1.0/i*4.0) , (1.0/j*4.0))*cos(iTime/100.0);
			vec3 starmap = texture( iChannel1, uv2, 1.0).xyz;
			float xdist = distance(starmap.x*1.0, x*1.0);
			float ydist = distance(starmap.y*1.0, y*1.0);

			float ztest = starmap.z / 500.0;

			if (xdist < ztest && ydist < ztest)
			{
				br += 1.0/starmap.z;

			}
		}
	}

	vec4 starcolor = vec4(br, br, br, 1.0);
	return starcolor;

}

vec4 intTest2(vec2 zap)
{
	float x = zap.x;
	float y = zap.y;

	float br = 0.0;
	float t = iTime;




		for (float j = 0.0; j < 32.0; j++)
		{
			vec2 uv2 = vec2(1.0 / (x+j), 1.0/(y+j) )*cos(t/20.0);
			vec3 starmap = texture( iChannel1, uv2, 1.0).xyz;
			float xdist = distance(starmap.x, x*1.0);
			float ydist = distance(starmap.y, y*1.0);

			float ztest = starmap.z * j /y * (x*y);

			if (xdist < ztest && ydist < ztest)
			{
				br += starmap.z;
				vec4 starcolor = vec4(starmap.x, starmap.z, starmap.y, 1.0);
				return starcolor;

			}
		}

	vec4 starcolor = vec4(br, br, br, 1.0);
	return starcolor;

}

