void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 mouse=iMouse.xy/iResolution.xy;//enable moving
	float gscale=0.7;//iMouse.x/iResolution.x*2.0;//uncomment to enable scaling
	/*uv*=1000.0;	//uncomment to make the image "pixelated"
	uv.x = float(int(uv.x)/10);
	uv.y = float(int(uv.y)/10);
	uv/=100.0;*/
	float c = 0.0;
	const int passes1 = 3;
	const int passes2 = 8;
	float div = 0.0;
	for(int i=1;i<=passes1;i++){
		float scale = float(i)*.5;
		c+=texture(iChannel0,0.1*gscale*vec2((float(i)*(mouse.x + uv.x)),(float(i)*(mouse.y + uv.y)))).r/scale;
		div+=1.0/scale;
	}
	for(int i=1;i<=passes2;i++){
		float scale = float(i);
		c+=texture(iChannel0,gscale*vec2((float(i)*(mouse.x + uv.x)),(float(i)*(mouse.y + uv.y)))).r/scale;
		div+=1.0/scale;
	}
	c/=div;
		fragColor = vec4(c);
}
