float rand(vec2 n)
{
  return 1. * 
     fract(sin(dot(n.xy, vec2(12.9898, 78.233)))* 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec3 c = vec3(0.);

	uv.x += iTime*0.001;


	uv = vec2(
		cos(sin(iTime*1.456))*uv.x + sin(cos(iTime*2.178642))*uv.y,
		sin(cos(iTime*1.456))*uv.x - cos(sin(iTime*2.178642))*uv.y);


	float pixel = texture(iChannel0, uv).x;

	if (rand(uv) < pow(pixel, 16.)) {
		c = vec3(1.);	
	}


	fragColor = vec4(c,1.);

}
