void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float s = max(1.0, floor(iMouse.x / iResolution.x*25.0));

    if(iMouse.x == 0.0) // Browse preview
    {
        s = 10.0;
    }

	float u = (floor(fragCoord.x/s)*s) / iResolution.x;
    float v = (floor(fragCoord.y/s)*s) / iResolution.y;
    vec4 channelColor = texture(iChannel0, vec2(u, 1.-v));
	fragColor = channelColor;
}
