void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float t = iTime;

  vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;

  float z = 1.0 - pow(p.x,0.4);

  float w = 0.0;

  w = mix(
      smoothstep(0.8, 1.0, z),
      z, 0.5 + (sin(t*10.0+(sin(t/2.0)*60.0)) * 0.5)
  );

  fragColor = vec4(w,w,w,1.0);
}
