void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Screenspace stuff
	vec2 uv = fragCoord.xy / iResolution.xy;	// the usual
    float screen = sign(uv.x-.5);				// -1 for left eye, +1 for right eye
    uv.x = mod(uv.x,0.5)*2.;					// actually split the screen into two 0..1 ranges
    uv = (uv-.5);								// centre the coordinate system
    uv.y /= (iResolution.x/2.)/iResolution.y;	// add vertical space for aspect ratio thing

    // draw something for a simple demo I guess
    uv.x += screen*(0.15+0.05*sin(iTime));							// shift things inward, push it into the distance
    float circle = step(length(uv),0.2+0.03*sin(iTime));		// draw a circle
    vec3 c = mix(vec3(.7,.7,1.),vec3(1.,.1,.0),circle);	// colours things in

	fragColor = vec4(c,1.0);
}
