#define time iTime / 10.


//Comment out CLEAR for just black and white
#define CLEAR

float random(int i) {
    return sin(float(float(i)+time*3.) *3.1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy);
    vec2 p = -1.0 + 2.0 * uv;
    p.x *= iResolution.x / iResolution.y;


    vec3 col = vec3(0.);
    vec2 loc = p/2.;


    float radius = .42;


    for (int i = 0; i < 58; ++i) {
        //get color
        float r = smoothstep(radius, radius+.004, distance(p,loc));

        col = 1.0 - col * r;


        //move circles
        float dx = .01 * (2. * cos(random(i) * 1.3) - 1.);
        float dy = sin(time * 10.) / 100.;
        loc += vec2(dx, dy);

        //make smaller
        radius -= .008;
    }

    #ifdef CLEAR
    if (col.r < .1 && distance(p, vec2(0.,0))> .8) {
        col = vec3(.1, .1, .4);
    }
	#endif


	fragColor = vec4(sqrt(col), 1.0);
}
