#define PI 3.14159265

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

	float amp = 1.-uv.y;
	amp = amp*amp;						// amplitude
	float freq = 0.19*fragCoord.x;	// frequency

	float c = amp*sin(PI*uv.x*uv.x*freq);

	c = (c + 1.0)*0.25;					// map to 0..1
	c = pow( c, 0.45 );					// correct for gamma

	fragColor = vec4(c,c,c,1.0);		// emit
}
