#define sampleRate 44100.0
#define tau (2.0 * 3.14159265359)

//Implementation of SubWah on http://wavepot.com/ 

float sub( float wave, float mul, float t)
{
  return sin(wave * mul + tau * t);
}

float msin(float x, float t)
{
  return sin(tau * t * x);
}

float absT( float x )
{
  if(x < 0.0)
    return -x;
  else
  	return x;
}

float fracT(float x)
{
    return x - (floor(x));
}

float modT( float x, float y)
{
  return x - (( (x/y) - fracT(x/y)) * y) ;  
}

float tri(float x, float t)
{

  return absT( modT(1.0 - (2.0 * t * x), 2.0) ) * 2.0 - 1.0;
}

vec2 soundOne( float t )
{

    float n = sampleRate / 341.5;
    float bass_osc = 0.8 * tri(n/3.0, t) + 0.05 * msin(n*2.0, t);

    float lfo 		= msin(0.18, t);
    float lfo_mul 	= msin(0.04, t);
    float bass_sub 	= 0.8 * sub(bass_osc, 2.0 + ((1.0 + lfo) * 
                     (2.0 + (1.0 + lfo_mul) * 12.0) ), t) + 0.7 * msin(n, t);

    float res =  0.4 * bass_sub;

    return vec2(res);
}


vec2 mainSound( in int samp,float time)
{
    return soundOne(time);
}

