#define DYNAMIC_GRID true //If true grid iterates from 0 to GRIDSIZE_X/Y looped
#define TIME_ITERATION 149 //If GRID false mark in what iteration we want to be
#define VELOCITY 1000.0 //Velocity of iterations
#define GRIDSIZE_X 150 //Max Grid size in X axis
#define GRIDSIZE_Y 150 //Max Grid size in Y axis

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    //Modify UV with time to create grid
    int time;
    if(DYNAMIC_GRID) time = int(iTime*VELOCITY);
    else time = TIME_ITERATION;
    uv.x*=float(time%GRIDSIZE_X);
    uv.y*=float(time%GRIDSIZE_Y);

    fragColor = texture(iChannel0,uv);
}
