renderpass.code:
vec3 circbands(vec2 uv, vec2 middle2)
{
    float dist2 = distance(uv, middle2)*30.0;
    vec3 col2 = vec3(dist2);
    col2 = fract(col2);
    col2 = step(0.5,col2);
    col2 = 1.0-col2;
    return col2;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 middle1 = vec2(0.5+(sin(iTime)*0.51),0.5 +(cos(iTime)*0.5));
    vec2 middle2 = vec2(0.5+(cos(iTime)*0.13),0.5 +(sin(iTime)*0.5));

    vec3 col = vec3(circbands(uv, middle1));
    vec3 col2 = vec3(circbands(uv, middle2));


    col -= col2;

    fragColor = vec4(col,1.0);
}
