vec3 h2rgb(float h)
{
    return clamp(2.0-abs(mod(h*3.0+vec3(0.0,0.0,2.0),3.0)-vec3(2.0,1.0,2.0))*2.0,0.0,1.0);
}


#define pi asin(1.)*2.
void lineSegment(inout vec4 fragColor, vec2 z, vec2 a, vec2 b, vec4 c,  float w){

    z -= a; b -= a;                        // go to A referential
    float q = dot(z, b) / dot(b, b) ;      // projection of P on line AB: normalized ordinate
    b *= clamp(q, 0., 1.);                 // point on segment AB closest to P 


    fragColor += vec4(c.xyz,1) * c.w*clamp(w-length( z - b),0.,1.);
}

void mainImage( out vec4 o, in vec2 i )
{
    o-=o;
    float s = iResolution.y / 10.;
    vec2 ii = i-iResolution.xy/2.;

    vec2[8] units;


    for(float dims = floor((1.-iMouse.x/iResolution.x)*6.9); dims >= floor((iMouse.y/iResolution.y)*6.9); dims--){
        float radius = s * dims + 10.;

        if(length(ii)<radius){
            for(float d = 0.; d < dims; d++){


                units[int(d)] = sin((d+.5)/dims*pi+.00001*vec2(iTime*(47.*d*d-1701.*d+10181.),iTime*(103.*d*d-4707.*d+50383.))+vec2(0,pi/2.));

            }
            float verts = pow(2.,dims);
            for(float v = 0.; v < verts; v++){
                vec2 p = vec2(0);
                for(float d = 0.; d < dims; d++){
                    p += units[int(d)]*(bool((int(v) & int(pow(2.,d))))?1.:-1.);

                }

                for(float d = 0.; d < dims; d++){
                    vec2 pp = p - 2.*units[int(d)]*(bool((int(v) & int(pow(2.,float(d)))))?1.:-1.);
                    lineSegment(o,ii,p*s, pp*s,vec4(h2rgb(1.-dims/6.),.25),1.3);
                }
            }
        }
    }





}
