vec3 Gradient3 (float y, vec3 c1, vec3 c2, vec3 c3) {

    float y1 = y * 2.;
    float y2 = y * 2. - 1.;

    vec3 g, g2, g3;

    g  = mix(c1, c2, y1) * step(y1, 1.);
    g2 = mix(c2, c3, y2) * step(y2, 1.) * step(1., y1);

    g = g + g2;
    return g;
}

vec3 Gradient4 (float y, vec3 c1, vec3 c2, vec3 c3, vec3 c4) {

    float y1 = y * 3.;
    float y2 = y * 3. - 1.;
    float y3 = y * 3. - 2.;

    vec3 g, g2, g3;

    g  = mix(c1, c2, y1) * step(y1, 1.);
    g2 = mix(c2, c3, y2) * step(y2, 1.) * step(1., y1);
    g3 = mix(c3, c4, y3) * step(y3, 1.) * step(1., y2);

    g = g + g2 + g3;
    return g;
}

vec3 Gradient5 (float y, vec3 c1, vec3 c2, vec3 c3, vec3 c4, vec3 c5) {

    float y1 = y * 4.;
    float y2 = y * 4. - 1.;
    float y3 = y * 4. - 2.;
    float y4 = y * 4. - 3.;

    vec3 g, g2, g3, g4;

    g  = mix(c1, c2, y1) * step(y1, 1.);
    g2 = mix(c2, c3, y2) * step(y2, 1.) * step(1., y1);
    g3 = mix(c3, c4, y3) * step(y3, 1.) * step(1., y2);
    g4 = mix(c4, c5, y4) * step(y4, 1.) * step(1., y3);

    g = g + g2 + g3 + g4;
    return g;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 c1 = vec3(124./256., 186./256., 197./256.); // blue 1
    vec3 c2 = vec3(58./256., 79./256., 127./256.);   // blue 2
    vec3 c3 = vec3(235./256., 75./256., 96./256.);   // pink
    vec3 c4 = vec3(242./256., 240./256., 245./256.); // white
    vec3 c5 = vec3(232./256., 230./256., 145./256.); // yellow

    //vec3 c = Gradient3(uv.y, c1, c2, c3);
    vec3 c = Gradient4(uv.y, c1, c2, c3, c4);
    //vec3 c = Gradient5(uv.y, c1, c2, c3, c4, c5);


	fragColor = vec4(c.rgb,1.0);
