void mainImage( out vec4 f, vec2 g )
{
	vec2 p = g /= 80.,k; f-=f-6.;

    for(;f.x-->0.;)
        for(f.y=6.;f.y-->0.;
           p = f.xy - 3.,
           p += sin( iTime + 9. * fract(sin((floor(g)+p)*mat2(2,5,5,2)))) - fract(g),
           f.z = max(abs(p.x)*.87 - p.y*.5, p.y))
                if (f.z < f.w)
                    f.w = f.z, k = p;

    //f = vec4(3,2,1,1) * f.w * sign(f.w - vec4(vec2(-k.x,k.x)*.87-k.y*.5,k.yy)); // beautifull version but 293c :(     
    f = f.w - sign(f.w - vec4(vec2(-k.x,k.x)*.87-k.y*.5,k.yy));
}
