void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 effect = vec2((iResolution.x / iResolution.y)*0.5,0.5);
    vec2 uvx = (fragCoord.xy / iResolution.y)-effect;
    float dist = length(uvx);
    if (dist > abs(sin(0.3)))
    {
        fragColor =vec4(0.9,0.9,0.9,1.0);
        return;
    }
    if (dist < abs(sin(0.25)))
    {
        fragColor =vec4(0.9,0.9,0.9,1.0);
        return;
    }
   	vec2 uv = fragCoord.xy / iResolution.xy;
    if (mod(iTime,8.0) <4.0) {
    if (mod(atan(uvx.y/uvx.x),4.0) <mod(iTime,4.0)) {
       	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
        return;
    }
    } else {
    if (mod(atan(uvx.y/uvx.x),4.0) <4.0-mod(iTime,4.0)) {
       	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
        return;
    }
    }
    fragColor = vec4(0.9,0.9,0.9,1.0);

}
