float pattern0(vec2 p, float time) { return (sin(length(p)*50.0+abs(sin(atan(p.y,p.x)*10.0+time*4.0)*length(p)*5.0)+time*10.0)+1.0)/2.0; }
float pattern1(vec2 p, float time) { return (sin((abs(p.x)+abs(p.y))*50.0+time*10.0)+1.0)/2.0; }
float pattern2(vec2 p, float time) { return sin(atan(p.y,p.x)*20.0+time*20.0); }

int getPosition(vec2 p) {
	if (p.y < -0.8 && abs(p.x) < 0.3) {
		if (p.x < -0.1) return 0;
		else if (p.x > 0.1) return 2;
	}
	return 1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
	int mp = getPosition((2.0*iMouse.xy-iResolution.xy)/iResolution.y);
	int pp = getPosition(p);

	if (p.y < -0.8 && abs(p.x) < 0.3) {
		if (pp == 0) fragColor = vec4(1.0,0.0,0.0,1.0);
		else if (pp == 1) fragColor = vec4(0.0,1.0,0.0,1.0);
		else fragColor = vec4(0.0,0.0,1.0,1.0);
		return;
	} else p.y -= 0.1;

	float p0,p1;

	if (mp == 0) {
		p0 = pattern0(p,-iTime);
		p1 = pattern0(p, iTime);
	} else if (mp == 1) {
		p0 = pattern1(p,-iTime);
		p1 = pattern1(p, iTime);
	} else {
		p0 = pattern2(p,-iTime);
		p1 = pattern2(p, iTime);		
	}

	float s = mix(p0,p1,smoothstep(0.5,0.55,length(p)));
	fragColor = vec4(vec3(s),1.0);
}
