void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;

    float t = iTime;
    float bpm = 1.0 / 60.0 * 85.0;
    float pi = 3.1415926535;
    float mt0 = sin((t + 0.4) * bpm * pi);
    float mt1 = sin((t + 0.12) * bpm * pi / 6.0);

    float tick = mt0;
    float pluckSoft = pow(abs(mt1), 32.0);
    float pluckHard = step(0.99, pow(abs(mt1), 24.0));
	float pluckSum =
        texture(iChannel0, vec2(0.6, 0.25)).x +
        texture(iChannel0, vec2(0.7, 0.25)).x +
        texture(iChannel0, vec2(0.8, 0.25)).x +
        texture(iChannel0, vec2(0.9, 0.25)).x +
        texture(iChannel0, vec2(1.0, 0.25)).x;
    pluckSum /= 0.5;

    float intense = pluckSum;
    float wave = texture(iChannel0, vec2(abs(nuv.x * 0.2), 0.75)).x;
    float fft = texture(iChannel0, vec2(abs(nuv.x * 0.5), 0.25)).x;
    float line = wave * intense * pow(fft, 6.0);

    fragColor.xyz += vec3(0.1, 0.4, 0.1) * clamp(t * 0.002, 0.0, 1.0);
    fragColor.y += pow(line, 1.3) * 1.5;

    fragColor.z += sin(length(nuv * 200.0) + t) * 0.2 * wave + intense * pluckHard;

    fragColor.xyz += vec3(0.2, 1.0, 0.6) * pluckSoft * 0.2 * intense * (1.0 - abs(nuv.y));
    fragColor.xyz += vec3(0.2, 1.0, 0.6) * pluckHard * 0.6 * intense * (1.0 - abs(nuv.y));
}
