float hue2rgb(float f1, float f2, float hue) {
    if (hue < 0.0)
        hue += 1.0;
    else if (hue > 1.0)
        hue -= 1.0;
    float res;
    if ((6.0 * hue) < 1.0)
        res = f1 + (f2 - f1) * 6.0 * hue;
    else if ((2.0 * hue) < 1.0)
        res = f2;
    else if ((3.0 * hue) < 2.0)
        res = f1 + (f2 - f1) * ((2.0 / 3.0) - hue) * 6.0;
    else
        res = f1;
    return res;
}

vec3 hsl2rgb(vec3 hsl) {
    vec3 rgb;

    if (hsl.y == 0.0) {
        rgb = vec3(hsl.z); // Luminance
    } else {
        float f2;

        if (hsl.z < 0.5)
            f2 = hsl.z * (1.0 + hsl.y);
        else
            f2 = hsl.z + hsl.y - hsl.y * hsl.z;

        float f1 = 2.0 * hsl.z - f2;

        rgb.r = hue2rgb(f1, f2, hsl.x + (1.0/3.0));
        rgb.g = hue2rgb(f1, f2, hsl.x);
        rgb.b = hue2rgb(f1, f2, hsl.x - (1.0/3.0));
    }   
    return rgb;
}

float psin(float x) {
    return sin(x) * 0.5 + 0.5;
}

float hue(float h, float a) {
    return mod(h * 0.1 + a, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 pos = vec2(fragCoord);
    vec2 uv = fragCoord.xy / iResolution.xy;

    float scl = 500.0;
    pos = scl * uv;

    float s = sin(iTime * 0.4) * 3.0 * scl * 0.05;
    float x = pos.x + s * sin(pos.y * 0.03 + sin(iTime) * 1.1213);
    float y = pos.y + s * sin(pos.x * 0.03 + iTime * 1.1534);

    pos = vec2(x, y);

    float dist = 5.0 * length(uv - vec2(0.5, 0.5));

    float w = scl; //iResolution.x;
    float h = scl; //iResolution.y;

    float b = 1.0 - 1.0 * min(1.0, dist * 0.00075);

    fragColor = vec4(0);

    fragColor += vec4(hsl2rgb(vec3(
        hue(iTime, 0.0),
        0.5 + 0.5 * psin(iTime * -0.74),
        b * psin(0.05 * dist * 0.5 - 2.0 * iTime + 3.14)
    )), 1.0);

    fragColor += vec4(hsl2rgb(vec3(
        hue(iTime, 0.1),
        0.5 + 0.5 * psin(iTime * -0.63),
        b * psin(0.05 * dist * 0.5 - 2.0 * iTime)
    )), 1.0);

    fragColor += vec4(hsl2rgb(vec3(
        hue(iTime, 0.3),
        1.0,
        0.5 * pow((1.0 - b), 2.0) * psin(0.05 * dist * 1.0 + 10.0 * iTime + 3.14)
    )), 1.0);

    fragColor += vec4(hsl2rgb(vec3(
        hue(iTime, 0.2),
        1.0,
        0.5 * pow((1.0 - b), 2.0) * psin(0.05 * dist * 1.0 + 10.0 * iTime)
    )), 1.0);

    float o = 3.14 * sin(dist) * 2.0 * 3.14;

    s = 0.1 + 0.9 * pow((
        psin(o + 0.015 * (pos.x+100.0*psin(iTime+pos.x*0.02-o) + pos.y+5.0*psin(iTime-o*0.34))) +
        psin(o + 0.015 * (pos.x - pos.y+50.0+50.0*psin(-iTime+pos.y*0.02+o)))
    ), 1.0);

    s = pow(s, 3.0 + 2.0 * psin(iTime * 0.743 + dist * 0.001));

    fragColor = fragColor * vec4(s, s, s, 1.0);
}

