// LMB rotates and changes speed.
float spinmulti;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float aspect = iResolution.x / iResolution.y;
	float radius = .3, border = 0.02;
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 sp = 1.0 - 2.0 * uv;
	sp.x *= aspect;
	float r = sqrt(dot(sp,sp));
	float f = (1.-sqrt(abs(1.0-r)))/(r);
	vec2 nuv;
	nuv.x = sp.x*f;
	nuv.y = sp.y*f;

	vec2 center = uv - vec2(.5,.5);
	spinmulti = (iResolution.x*.5 - iMouse.x ) / (iResolution.x*2.);

	if(iMouse.z>0.) {
		nuv += vec2(spinmulti, 0.0);
	} else
		nuv += vec2(iTime*spinmulti, 0.0);

	vec2 tr = uv - vec2(1,1);
	center.x *= aspect;
	float dist		= length(center);

	float d_bl = sqrt(dot(uv,uv));
	float d_tr = sqrt(dot(tr,tr));

	vec4 col = vec4(1.0); 
	col.r = sin(iTime)*.7*sin(d_tr*5.)+mix(.2,.4,dot(cos(fragCoord.x), cos(fragCoord.y)));
	col.b = d_bl ;
	col.g = sin(dist) * sin(iTime) + cos(iTime) * 0.5;		

	if (dist < radius) {
		vec4 tex = vec4(texture(iChannel0, nuv*6.));
		fragColor = tex;
		fragColor.rgb += col.rgb*0.3,
			fragColor +=  texture(iChannel1, vec2(uv.x,0.75)).x;
		vec2 fr = texture(iChannel1, vec2(uv.x,0.25)).xy;
		fr.x = log(dot(fr.x, fr.y));
		fragColor *= .7 - smoothstep(.5, .6, fr.x);

	} else
		fragColor = vec4(col.r, col.g, col.b, 1.);
}
