float PI = 3.14;

vec2 rotate(vec2 v, float angle) {
	angle = angle * PI / 180.0;
	return vec2(cos(angle)*v.x - v.y*sin(angle),
				cos(angle)*v.y + v.x*sin(angle));	
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	float zoom = (2.0+sin(iTime))/3.0;
	vec2 as = vec2(iResolution.y / iResolution.x, 1.0);

	vec2 zoomVec = vec2(zoom, zoom);
	vec2 coords = fragCoord.xy / iResolution.xy;
	coords -= vec2(0.5, 0.5);
	coords /= as;
	coords += vec2(1.0+sin(iTime), 1.0+sin(iTime));
	coords *= zoomVec;

	coords = rotate(coords, 45.0 * iTime);

	vec4 pixel = texture(iChannel0, mod(coords+vec2(0.5, 0.5), 1.0));
	fragColor = pixel;
}
