float circle(vec2 uv, float diameter, float speed)
{
	speed *= sin(iTime*6.66-0.9);
	float angle = iTime*speed-3.4*length(uv)*sin(iTime*0.66);
	uv*= mat2(  sin(angle), cos(angle),
				cos(angle),-sin(angle));

	float pixelate = 0.07-sin(iTime*0.1)*0.045;
	//pixelate -= mod(pixelate,0.001);
	uv -= mod(uv,pixelate)-pixelate*0.5;
	return 1.0-smoothstep(0.0002,0.0001+pixelate,(abs(mod(length(uv),0.05)-diameter)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy-iResolution.xy/2.0) / min(iResolution.x,iResolution.y);

	fragColor = vec4(circle(uv +0.012*vec2(sin(iTime*0.81),sin(iTime*0.34)), 0.008,  0.023),
						0.3*circle(uv +0.012*vec2(sin(iTime*0.72),sin(-iTime*0.63)), 0.02,   0.022),
						0.2*circle(uv +0.012*vec2(sin(-iTime*0.623),sin(iTime*0.53)), 0.032,  0.021),
						1.0);
}

#define PI2 6.66
#define speed 6.66

vec2 RandomTune(float time)
{
    float ts = time*speed;

    float note = float(int(
        sin(.666*float(int(ts)))
        *6.66+
        cos(6.66*float(int(ts)))
        *6.66+
        cos(66.6*float(int(ts)))
        *6.66
            ));

    float freq = 666.0*pow(2.0,note/12.0);
    float decay = 1.0-mod(ts,1.0);

    float wav = sin(freq*PI2*time+2.0*sin(time*time*freq*.666))*decay;
    return vec2(wav*sin(time),wav*cos(time));
}

vec2 mainSound( in int samp,float time)
{
  //time += 6.66;
  return 0.5*(RandomTune(time)+ RandomTune(time-.0666).yx*0.666);
}
