// my first attempt at a shader on shadertoy
// do whatever you want with this code
// i invented the algorithm in the 80s
// in those days it took several minutes to render
// one frame (vga?) but i made amachine code version
// only 256x256 and x and y were 8 packed
// into a single 16bit register iirc i could
// draw some animations real time.
// alexis farmer

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float miTime = iTime*5.0 * 0.7;
	vec2 pos1 = fragCoord.xy / iResolution.xy;
	vec4 blah=texture(iChannel0, vec2(0.0,0.0));
	vec4 blah2=texture(iChannel0, vec2(0.5,0.5));
	float mydist= 512.0*(sin(0.011*miTime));
	vec2 pos2 = vec2((fragCoord.xy + vec2(mydist,mydist))/ iResolution.xy);

	pos1 = (pos1-vec2(0.5,0.5)) * 4.0*(sin(0.1*miTime));
	pos2 = (pos2-vec2(0.5,0.5)) * 4.0*(sin(0.1*miTime));
	pos1.y = pos1.y * (0.5+0.2*blah.x);
	pos2.y = pos2.y * (0.5+0.2*blah.x);
	vec2 add = vec2(0.1,0.0);
	vec2 mul = vec2(0.0,0.0);
	mul = mul - 0.5 + 0.5*(blah.x);
	mul.x = mul.x+ 0.1*(sin(0.012*miTime));
	mul.y = mul.y+ 0.1*(sin(0.0013*miTime));

	mul.y=mul.y*(1.0+1.0*sin(0.51*miTime));
	mul.x=mul.x*(1.0+1.0*sin(0.126*iTime));

	add.x=add.x+0.01*sin(0.5*0.127*miTime);
	add.y=add.y+0.01*sin(0.25*0.129*miTime);

	float dist;

	int iters=int(32.0*(1.0+sin(0.103*iTime)));

	for(int i=0;i<32;i++)
	{
		if (i<iters)
		{
			pos1.x=pos1.x+(pos1.y-0.5)*mul.x+add.x;
			pos2.x=pos2.x+(pos2.y-0.5)*mul.x+add.x;
			pos1.x=fract(pos1.x);
			pos2.x=fract(pos2.x);
			pos1.y=pos1.y-(pos1.x-0.5)*mul.y+add.y;
			pos2.y=pos2.y-(pos2.x-0.5)*mul.y+add.y;
			pos1.y=fract(pos1.y);
			pos2.y=fract(pos2.y);
		}
	}
	dist = clamp(distance(pos1,pos2),0.0,1.0);
	dist = dist * dist;
	fragColor = vec4(2.0*dist*blah.x,dist*blah.x,dist*blah2.x,1.0);
}
