// Drawing some randomized squares.
// by Eerik Kukkonen

#ifdef GL_ES
precision mediump float;
#endif

// Random function
float random (vec2 st) { 		
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))* 
        43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy; 		// Normalize the coordinates
//    vec2 st = iResolution.xy/fragCoord.xy;	// Mixing the coordinate system on purpose
    st.x = fragCoord.x/iResolution.y;			// Get the proportions 1 by 1 for the squares

    st.x *= 10.0 - sin(iTime) * 5.0;		// Scale the st to get bigger squares.	
    st.y *= 10.0 - sin(iTime) * 5.0; 		// Using sin(time) to get some zooming motion.
    											// Multiplying by 5.0 to get some more motion happening.

    vec2 ipos = floor(st);  // Get integer coordinates for drawing the squares.

    // Get random blue value based on the ipos coordinates and the time.
    // It's important to give both x and y to get a random color for a single square.
    vec3 color = vec3(1.0, 0.5, random( vec2(ipos.x, ipos.y) + (iTime) )); 

    fragColor = vec4(color,1.0);
}

// Jep tuli jonkin verran luettua ja selattua Book of Shadersia.
// Random-osion pohjalta sitten tein tätä shaderia. Piirtelin jossain vaiheessa ympyrää
// Youtube-tutoriaalin perusteella.

// Itsearviointi: 3

// Mielestäni tämä voisi olla oikea arvosana palautuksestani. Käytän tehtävässä ajan
// uniformia kahdessa paikassa saadakseni hieman liikettä kuvaan. Kuitenkaan tämä
// ei ehkä edistyneisyyden puolesta riitä 5 pisteen suoritukseen.
