// leon 11/03/2017

float luminance (vec4 c) { return (c.r+c.g+c.b)/3.; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 unit = 1. / iResolution.xy;

    vec4 frame = texture(iChannel0, uv);
    vec4 video = texture(iChannel1, uv);

    float angle = luminance(frame) * 3.1459 * 2.;
    angle += iTime * 0.1;
    angle += luminance(video) * 3.14159 * 2.;
    vec2 offset = vec2(cos(angle), sin(angle)) * unit;

    frame = texture(iChannel0, uv + offset);

    vec4 color = mix(frame, video, smoothstep(.4, .6, length(video-frame)));
    fragColor = color;
}
