void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float fpos = floor(fragCoord.x / 50.0);
    float time = mod(iTime, 8.0);

	float vstripe = mod(fpos, 2.0);
    fragColor = vec4(vec3(vstripe), 1.0);

    float cutoff = (sin(fpos * 0.2 - time * 3.0) * 0.5 + 0.5) * 0.5 + (time * 0.5) - 0.5;
    if (fragCoord.y / iResolution.y > cutoff) {
        fragColor = vec4(vec3(0.0), 1.0);
    }

    if (time >= 2.0) {
        time -= 2.0;

        float fpos2 = fpos + 25.0;

        float vstripe2 = mod(fpos2, 2.0);
        if (vstripe2 == 1.0) {
            fragColor = vec4(vec3(vstripe2), 1.0);

            float cutoff = (sin(fpos2 * 0.4 - time * 4.0) * 0.5 + 0.5) * 0.5 + (time * 0.5) - 0.5;
            if (fragCoord.y / iResolution.y > cutoff) {
                fragColor = vec4(vec3(0.0), 1.0);
            }
        }

        if (time >= 2.0) {
            time -= 2.0;

            fpos = floor(fragCoord.y / 50.0);

            float hstripe = mod(fpos, 2.0);
            if (hstripe == 1.0) {
                float cutoff = (sin(fpos * 0.2 - time * 4.0) * 0.5 + 0.5) * 0.5 + (time * 0.5) - 0.6;

                if (fragCoord.x / iResolution.x < cutoff) {
                	fragColor = vec4(vec3(0.0), 1.0);
                }
            }

            if (time >= 1.5) {
                time -= 1.5;

                fpos2 = fpos + 25.0;

                float hstripe2 = mod(fpos2, 2.0);
                if (hstripe2 == 1.0) {
                    float cutoff = (sin(fpos * 0.7 - time * 2.0) * 0.5 + 0.5) * 0.3 + (time * 0.7) - 0.6;

                    if (fragCoord.x / iResolution.x < cutoff) {
                        fragColor = vec4(vec3(0.0), 1.0);
                    }
                }
            }
        }
    }
}
