void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 nuv = uv * 2.0 - 1.0;
	float t = iTime;
    float pi = 3.14159265359;
    float bpm = 1.0 / 60.0 * 130.0;

    float bt = t * bpm;
    float m0 = texture(iChannel0, vec2(0.0, 0.25)).x;
    float m1 = texture(iChannel0, vec2(0.5, 0.25)).x;

    m0 = pow(m0, 2.0) * 2.5;
    m1 = pow(m1, 4.0);

    vec2 nnv = vec2(sin(bpm * t * pi * 2.0), pow(tanh(nuv.y * sin(t * pi * 4.0) * 25.0), 4.0));

    fragColor.xz = vec2(length(nnv)); 
    fragColor.xz *= sin(abs(nuv.x * 40.0) + bt * bpm * 10.0 * (1.0 - abs(nuv.y)));

    fragColor.xz *= pow(length(nuv), 0.7) + m0;
    fragColor.y += abs(nuv.y) * m1;

    fragColor.x *= texture(iChannel1, nuv * nuv.x * nuv.y + bt).x * -0.5 * pow(abs(length(nuv)) * 2.0 * nnv.y, 7.0);

    fragColor.xyz += texture(iChannel1, vec2(uv.x * 0.5 + bt, 0.0)).x * length(nuv) * 0.5 * m0;

    fragColor.xy += fract(bt) * 2.5 * abs(nuv.y) * m0;

    fragColor.xyz -= fragColor.xyz * (1.0 - m0);
}
