void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec2 centre = iResolution.xy / 2.0;

    vec4 col = vec4(0.0,0.0,0.0,1.0);

    float steps = 16.0;

    for(float x = -(steps/2.0); x < (steps/2.0); x++){
    	for(float y = -(steps/2.0); y < (steps/2.0); y++){
    		float dist = sqrt(sqrt((distance(fragCoord + vec2(x, y) / steps, centre))) * 100.0);
    		col += floor(mod(dist - iTime * 3.0, 2.0));
    	}
    }

    col /= steps * steps;

	fragColor = col * (1.0 - (distance(fragCoord, centre) / iResolution.x));
}
